import java.util.Scanner;


public class MultiplicationEx {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner a=new Scanner(System.in);
		int c=a.nextInt();
		for(int b=1;b<=10;b++)
		{
			System.out.println(c+"*"+b+"="+(c*b));
		}
		
	}

}
